import LandingPage from "./views/landingpage/LandingPage";
import RegisterPage from './views/registerpage/RegisterPage';
import NavbarTest from "./components/globalComponents/navTest/NavbarTest";
import LoginPage from "./views/loginpage/LoginPage";
import { HashRouter, Routes, Route, Navigate } from "react-router-dom";
import HowItWorksPage from "./views/howitworks/HowItWorksPage";
import SupportPage from "./views/support/SupportPage";
import CommunityPage from "./views/community/CommunityPage";
import MealsPage from "./views/mealspage/MealsPage";
import React, { useState, useEffect } from "react";
import CartPage from "./views/cartpage/CartPage";
import Footer from "./components/globalComponents/footer/Footer";
import './App.css';

export const MyContext = React.createContext();

function App ()
{
  const cartItems = JSON.parse( localStorage.getItem( "cart" ) ) || [];
  const userData = JSON.parse( localStorage.getItem( "data" ) ) || null;
  const [ meals, setMeals ] = useState( [] );
  const [ cart, setCart ] = useState( cartItems );
  const [ orders, setOrders ] = useState( [] );
  const [ user, setUser ] = useState( userData );
  const [ isLoggedIn, setIsLoggedIn ] = useState( false );
  const [ token, setToken ] = useState( false );
  const [ userId, setUserId ] = useState( "" );

  useEffect( () =>
  {
    const data = JSON.parse( localStorage.getItem( "data" ) );
    if ( data )
    {
      fetch( process.env.REACT_APP_SERVER_URL + "/user/verifytoken", {
        method: "POST",
        headers: {
          "token": data.token
        }
      } ).then( ( res ) =>
      {
        return res.json();
      } ).then( ( result ) =>
      {
        console.log( result );
        if ( result.success )
        {
          setIsLoggedIn( true );
          setToken( data.token );
          setUserId( data.id );
        } else
        {
          alert( result.message );
        }
      });
    }
    fetch( process.env.REACT_APP_SERVER_URL + "/meals" )
    .then( res => res.json() )
    .then( data =>
    {
      setMeals( data );
    } );
  }, [] );

  useEffect( () =>
  {
    localStorage.setItem( "data", JSON.stringify( user ) );
  }, [ user ] );

  useEffect( () =>
  {
    localStorage.setItem( "cart", JSON.stringify( cart ) );
  }, [ cart ] );


  /*  const logOut = () =>
      {
          localStorage.removeItem( 'data' );/
          setToken( false );
          setUserId( "" );
          setIsLoggedIn( false );
      };

      const deregister = async event =>
      {
          const settings = {
              method: "DELETE",
              headers: {
                  "Authorization": "Bearer " + token
              }
          };
          const response = await fetch( process.env.REACT_APP_SERVER_URL + `/users/${ userId }`, settings );
          //await fetch( `http://localhost:3001/users/ettings );
        const parsedRes = await response.json();
          try
          {
              // If the request was successful...
              if ( response.ok )
              {
                  alert( parsedRes.message );
                  setIsLoggedIn( false );
                  setUserId( "" );
              } else
              {
                  throw new Error( parsedRes.message );
              }
          } catch ( err )
          {
              alert( err.message );
          }
      };
  }
   */

  return (
    <MyContext.Provider value={ { meals, setMeals, cart, setCart, orders, setOrders, user, setUser, token, setToken, isLoggedIn, setIsLoggedIn, /* {logOut}, {deregister}  */ } }>
      <div className='App'>
        <HashRouter>
          <NavbarTest isLogged={ isLoggedIn } />
          <Routes>
            <Route path="/" element={ <LandingPage /> } />
            <Route path="/howitworks" element={ <HowItWorksPage /> } />
            <Route path="/support" element={ <SupportPage /> } />
            <Route path="/meals" element={ <MealsPage /> } />
            <Route path="/community" element={ <CommunityPage /> } />
            <Route path="/login" element={ <LoginPage /> } />
            <Route path="/register" element={ <RegisterPage /> } />
            <Route path="/cart" element={ <CartPage /> } />
          </Routes>
          <Footer />
        </HashRouter>
      </div>
    </MyContext.Provider>
  );
}
export default App;
