import LandingPage from "./views/landingpage/LandingPage";
import RegisterPage from './views/registerpage/RegisterPage';
import NavbarTest from "./components/globalComponents/navTest/NavbarTest";
import LoginPage from "./views/loginpage/LoginPage";
import { HashRouter, Routes, Route, Navigate } from "react-router-dom";
import HowItWorksPage from "./views/howitworks/HowItWorksPage";
import SupportPage from "./views/support/SupportPage";
import CommunityPage from "./views/community/CommunityPage";
import MealsPage from "./views/mealspage/MealsPage";
import React, { useState, useEffect } from "react";
import CartPage from "./views/cartpage/CartPage";
import Footer from "./components/globalComponents/footer/Footer";
import './App.css'

export const MyContext = React.createContext();

function App() {
  const cartItems = JSON.parse(localStorage.getItem("cart")) || [];
  const userData = JSON.parse(localStorage.getItem("data")) || null; 
  const [meals, setMeals] = useState([]);
  const [cart, setCart] = useState(cartItems);
  const [orders, setOrders] = useState([]);
  const [user, setUser] = useState(userData);
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [token, setToken] = useState(false);
  const [userId, setUserId] = useState(""); 

  useEffect(() => {
    const data = JSON.parse(localStorage.getItem("data"));
    if (data.id) {
      fetch(process.env.REACT_APP_SERVER_URL + "/verifytoken",{
        method:"POST",
        headers:{
          "token":data.token
        }
      }).then((res)=>{
        return
      })
      setIsLoggedIn(true);
      setToken(data.token);
      setUserId(data.id);
    }
  }, [])

  useEffect(()=>{
    localStorage.setItem("data", JSON.stringify(user))
   },[user])

  useEffect(()=>{
   localStorage.setItem("cart", JSON.stringify(cart))
  },[cart])

useEffect(()=>{
  console.log(`user: ${user}`)
  fetch(process.env.REACT_APP_SERVER_URL + "/meals")
  .then(res=>res.json())
.then(data=>{
 setMeals(data)
})
},[])

